
public class Declaraciones_Operadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  
		//*Declarar una variable *//
		
		int a=5;
		
		int b;
		b=7;
		
		//* Operador suma *//
		int c=b+a;
		
		//* Operador Incrementar en 1 *//
		c++;
		
		//c-=6 resta 6
		//c+=6 suma 6
		
		System.out.println(c);
		
	}

}
